import React from "react";
import "./styles/main.css";
import Video from "./Video";
import Comments from "./Comments";
import Queue from "./Queue";
import Description from "./Video-description";
import Axios from "axios";

const apiKey = "94d45313-a526-444a-ae41-67acf941a5e8";

class Main extends React.Component {
  videosApi = "https://project-2-api.herokuapp.com/videos/";

  state = {
    queue: [],
    currentVideo: {},
  };

  componentDidMount() {
    console.log("component mounted");
    this.getQueue();
    this.getVideo(0);
  }

  componentDidUpdate(prevProps) {
    console.log("component updated");
  }

  getQueue() {
    Axios.get(`${this.videosApi}/?api_key=${apiKey}`)
      .then((response) => {
        this.setState({ queue: response.data });
      })
      .catch((error) => console.log(error));
  }

  getVideo(videoId) {
    Axios.get(`${this.videosApi}/${videoId}/?api_key=${apiKey}`)
      .then((response) => {
        this.setState({ currentVideo: response.data.id[0] });
      })
      .catch((error) => console.log(error));
  }

  render() {
    return (
      <>
        <main className="main">
          <Video currentVideo={this.state.currentVideo} />
        </main>
        <main className="content">
          <div className="content__wrapper">
            <Description currentVideo={this.state.currentVideo} />
            <Comments comments={this.state.currentVideo.comments} />
          </div>
          <Queue queue={this.state.queue} />
        </main>
      </>
    );
  }
}

export default Main;
