import React from "react";
import "./styles/main.css";
import Video from "./Video";
import Comments from "./Comments";
import Queue from "./Queue";
import Description from "./Video-description";
import Upload from "./Upload";
import { Link } from "react-router-dom";
import Axios from "axios";

const apiKey = "94d45313-a526-444a-ae41-67acf941a5e8";

class Main extends React.Component {
  videosApi =
    "https://project-2-api.herokuapp.com/videos/?api_key=94d45313-a526-444a-ae41-67acf941a5e8";

  state = {
    videosList: [],
    video: {},
  };

  componentDidMount() {
    console.log("component mounted");
    this.getList();
    this.getVideo("1af0jruup5gu");
  }

  componentDidUpdate(prevProps) {
    console.log("component updated");
    const oldVideoId = prevProps.match.params.id;
    const newVideoId = this.props.match.params.id;
    console.log("oldVideoId", oldVideoId);
    console.log("newVideoId", newVideoId);
    if (newVideoId === oldVideoId) {
      console.log("post id matches, no need to update state");
    } else {
      console.log("fetching new post");
      this.getVideo(newVideoId);
    }
  }

  getList() {
    Axios.get(`${this.videosApi}`)
      .then((response) => {
        this.setState({ videosList: response.data });
      })
      .catch((error) => console.log(error));
  }

  getVideo() {
    Axios.get(`${this.videosApi}/data/?`);
  }

  render() {
    return (
      <>
        <main className="main">
          <Video currentVideo={this.state.currentVideo} />
        </main>
        <main className="content">
          <div className="content__wrapper">
            <Description currentVideo={this.state.currentVideo} />
            <Comments comments={this.state.comments} />
          </div>
          <Queue queue={this.state.queue} />
          <Upload />
        </main>
      </>
    );
  }
}

export default Main;
